<template>
  <Layout>
    <span
      class="flex flex-wrap flex-col items-center mx-auto c-content"
      v-html="aboutContent"
    ></span>
  </Layout>
</template>

<script>
import axios from 'axios';

export default {
  metaInfo: {
    title: "A propos",
  },
  //==================================================================================================================================================================================================================================================================================================
  //
  //  ####      ###    ######    ###
  //  ##  ##   ## ##     ##     ## ##
  //  ##  ##  ##   ##    ##    ##   ##
  //  ##  ##  #######    ##    #######
  //  ####    ##   ##    ##    ##   ##
  //
  //==================================================================================================================================================================================================================================================================================================

  data() {
    return {
      aboutContent: "",
    };
  },
  
  //
  //   ####  #####    #####    ###    ######  #####  ####
  //  ##     ##  ##   ##      ## ##     ##    ##     ##  ##
  //  ##     #####    #####  ##   ##    ##    #####  ##  ##
  //  ##     ##  ##   ##     #######    ##    ##     ##  ##
  //   ####  ##   ##  #####  ##   ##    ##    #####  ####
  //
  

  created() {
    axios
      .get(
        "https://public-api.wordpress.com/rest/v1.1/sites/assokyosai.wordpress.com/posts/297/"
      )
      .then((response) => {
        this.aboutContent = response.data.content;
      });
  },
};
</script>
